import React from "react";
import Gambar from "../componen/2.jpg";

function Hero() {
  return (
    <div className=" bg-cover bg-local text-basefont" style={{ backgroundImage: `url(${Gambar})`, backgroundPositionY: "0px" }}>
      <div className="flex h-screen flex-col items-center pt-[10rem] lg:pt-[15rem]">
        <div>
          <p className="text-4xl font-bold text-primary   lg:text-7xl ">Growing Together</p>
          <h1 className="text-[1.2rem] font-bold lg:p-2 lg:text-3xl">Grow With Data</h1>
          <div>
            <p className="lg:font-bold ">Fast, Flexible and Great</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Hero;
