import React from "react";
import Gambar from "../componen/1.jpg";
import Gambars from "../componen/3.jpg";

function Hero() {
  return (
    <div className="flex h-2/5 flex-col items-center bg-cover text-basefont">
      <img src={Gambar} alt="s" className="relative block h-screen w-full mix-blend-overlay lg:hidden" style={{ backgroundPositionY: "-20px" }} />
      <img src={Gambars} alt="s" className="relative  hidden h-screen  w-full mix-blend-overlay blur-[1px] lg:block" />

      <div className="absolute flex flex-col items-center pt-[160px] lg:pt-[12rem]">
        <p className="text-center text-4xl font-bold text-primary    lg:text-4xl">
          Growing Together <span className="lg:hidden">Everywhere</span>
        </p>
        <h1 className="pt-[5px] text-[15px] font-bold text-basefont lg:p-2 lg:text-7xl">Grow With Data</h1>
        <div>
          <p className="text-[18px] font-bold lg:animate-pulse lg:text-[25px]">
            Fast, Flexible and Great <span className="lg:text-4xl">Sans</span>
          </p>
        </div>
        <p className="px-20 py-2 text-center text-[11px] lg:block  lg:text-[15px]">Help anybody learn web programming and solving the problems, the motivation just for fun </p>
        <button className="mt-3 rounded-xl bg-primary px-5 py-2 text-[10px] font-bold text-black lg:px-14 lg:py-2 lg:text-[14px] ">Get Started</button>
      </div>
    </div>
  );
}

export default Hero;
