import React from "react";
import { useRef } from "react";

function Referensi() {
  const Element = useRef(null);

  const Handle = () => {
    Element.current.textContent = "Sudah Berubah";
  };
  return (
    <div>
      <p ref={Element}>Ini adalah elemennya</p> {/*yang akan berubah*/}
      <button onClick={Handle}>Klik disini</button>
    </div>
  );
}

export default Referensi;
