import React from "react";
import Gambar from "../componen/2.jpg";

function Hero() {
  return (
    <div className="flex h-screen flex-col items-center text-basefont">
      <img src={Gambar} alt="" className="relative h-screen w-full mix-blend-overlay" />
      <div className="absolute flex flex-col items-center pt-[180px] lg:pt-[12rem]">
        <p className="text-4xl font-bold text-primary   lg:text-7xl ">Growing Together</p>
        <h1 className="pt-[5px] text-[15px] font-bold text-gray-400 lg:p-2 lg:text-3xl">Grow With Data</h1>
        <div>
          <p className=" text-[18px] font-bold lg:text-[25px]">Fast, Flexible and Great</p>
        </div>
        <p className="px-20 py-2 text-center text-[11px] lg:block  lg:text-[15px]">Lorem ipsum dolor consectetur adipisicing elit. Esse ab vel sed! Lorem ipsum dolor sit amet.</p>
        <button className="mt-3 rounded-xl bg-primary px-5 py-2 text-[10px] font-bold text-black lg:px-14 lg:py-2 lg:text-[14px] ">Get Started</button>
      </div>
    </div>
  );
}

export default Hero;
